let jsonData,alertBox=document.querySelector("#alertBox"),jokeType=document.querySelector("#jokeType"),display=document.querySelector("#display p");function onloadTask(){alertBox.innerHTML="Select joke type to get any random joke of that type.",displayAlertBox(),getData(),display.innerText="Display"}function getData(){$.get("data_file.json",function(json){jsonData=json})}function checkJokeType(){if("general"==jokeType.value)for(;;){let randId=getRandom();if("general"==jsonData[randId].type)return`Joke Type:=${jsonData[randId].type} <br> ${jsonData[randId].setup} <br> ${jsonData[randId].punchline}`}else{if("programming"!=jokeType.value){let randId=getRandom();return`Joke Type:=${jsonData[randId].type} <br> ${jsonData[randId].setup} <br> ${jsonData[randId].punchline}`}for(;;){let randId=getRandom();if("programming"==jsonData[randId].type)return`Joke Type:=${jsonData[randId].type} <br> ${jsonData[randId].setup} <br> ${jsonData[randId].punchline}`}}}function getRandom(){return parseInt(Math.random()*jsonData.length)}function displayAlertBox(){$("#alertBox").fadeIn(1500,function(){$(this).delay(100).fadeOut(2e3)})}document.onload=onloadTask(),$("#getJoke").on("click",function(){let joke=checkJokeType();display.innerHTML=joke});